{{ ((chartType) => ( (() => {
    // Retrieve selected filter values
    const { selectedGrievanceageReport, chart_color_palette } = utills.getFilters();

    // Filter the dataset based on selected filters
    const filteredData = utills.filterDataPoints({
        data: grievancedataset_tnl.data,
        filterTypes: '*',
        excludeTypes: ['AgeReport']
    });

    // Define the order for age categories
    const ageCategoryOrder = [
        "0-10 days", "11-20 days", "21-30 days",
        "31-40 days", "41-50 days", "51-60 days",
        "61-70 days", "71-80 days", "81-90 days",
        "91-100 days", "100+ days"
    ];

    // Filter only the relevant statuses
    const relevantStatuses = ["In Progress", "Draft", "Verified", "In Review", "Send For Correction"];
    const statusFilteredData = filteredData.filter(req => relevantStatuses.includes(req.grievance_status));

    // Aggregate data by grievance age category and count grievances
    const aggregatedData = statusFilteredData.reduce((acc, req) => {
        const grievance_age_category = req.age_category || 'Unknown'; // Handle missing categories
        // Aggregate counts for each age category
        acc[grievance_age_category] = (acc[grievance_age_category] || 0) + 1; // Increment logic
        return acc; // Return the accumulator for the next iteration
    }, {});

    // Convert aggregated data into an array of objects in order of age categories
    const filteredDataWithNonZeroCounts = ageCategoryOrder
        .map(category => ({
            constituency_name: category,
            count: aggregatedData[category] || 0,
            color: selectedGrievanceageReport === category ? chart_color_palette.secondarylight : chart_color_palette.secondarylight // Adjusted color logic
        }))
        .filter(item => item.count > 0); // Only include categories with non-zero counts

    // Extract categories and values for the chart
    const categories = filteredDataWithNonZeroCounts.map(item => item.constituency_name);
    const values = filteredDataWithNonZeroCounts.map(item => ({
        value: item.count,
        itemStyle: {
            color: item.color,
            opacity: selectedGrievanceageReport && selectedGrievanceageReport !== item.constituency_name ? 0.2 : 1
        }
    }));

    // Return the final chart configuration
    return {
        title: {
            text: appsmith.URL.queryParams.lng === 'te' ? 'ఫిర్యాదుల వయో వర్గం నివేదికు' : 'Open Grievances by Age in Days',
            left: 'center',
            top: 'top',
            textStyle: {
                fontSize: 18,
                fontWeight: 'bold'
            }
        },
        grid: {
            left: '15%',
            right: '10%',
            top: '20%',
            bottom: '20%'
        },
        yAxis: {
            type: 'value'
        },
        xAxis: {
            type: 'category',
            data: categories // Only show categories with non-zero counts
        },
        series: [
            {
                type: 'bar',
                data: values,
                label: {
                    show: true,
                    position: 'top',
                    formatter: '{c}',
                    fontSize: 12
                }
            }
        ],
        tooltip: {
            trigger: 'axis',
            axisPointer: {
                type: 'shadow'
            }
        }
    };
})()
))(grievance_age_report_chart.chartType) }}
