{{ ((chartType) => ( (() => {
    // Retrieve selected filter values
    const { selectedGrievanceByAssociationCat, chart_color_palette } = utills.getFilters();

    // Filter the dataset based on selected filters
    const filteredData = utills.filterDataPoints({
        data: grievancesdataset_dev.data,
        filterTypes: '*',
        excludeTypes: ['ByAssociationCat', 'TimeLine']
    });

    // Use query binding to get association tags
    const associationTagsData = association_tags_check_dev.data || []; // Replace with your actual query name

    // Extract association tags to include in the filtering
    const associationTagsSet = new Set(
        associationTagsData.map(item => item.Tags ? item.Tags.trim() : '') // Adjust 'Tags' based on your actual property
    );
    console.log('Association tags:', associationTagsSet);

    // Aggregate data by association tags and count distinct grievances
    const aggregatedData = filteredData.reduce((acc, req) => {
        const uniqueGrievanceKey = req.grievance_key;
        req.requestor_tags?.forEach(tag => {
            if (associationTagsSet.has(tag.trim())) { // Check if the tag is in the associationTagsSet
                if (!acc[tag]) {
                    acc[tag] = new Set(); // Create a Set for distinct grievance keys
                }
                acc[tag].add(uniqueGrievanceKey); // Add the grievance key to the Set
            }
        });
        return acc;
    }, {});

    console.log('Aggregated tags:', aggregatedData);

    // Convert the aggregated data into an array, count distinct grievances, and sort
    const sortedData = Object.entries(aggregatedData)
        .map(([associationTags, grievanceSet]) => ({
            associationTags,
            count: grievanceSet.size
        })) // Get distinct count
        .filter(item => item.associationTags !== null && item.associationTags !== 'None' && item.associationTags !== '') // Exclude null, None, and ''
        .sort((a, b) => b.count - a.count); // Sort in descending order by count

    // Take the top 5 and aggregate the rest as "Others"
    const top5 = sortedData.slice(0, 5); // Get the top 5 categories
    const othersCount = sortedData.slice(5).reduce((acc, curr) => acc + curr.count, 0); // Sum up counts for "Others"

    // Prepare final data, ensuring "Others" is included if there's any count
    let finalData = [
        ...top5,
        ...(othersCount > 0 ? [{ associationTags: 'Others', count: othersCount }] : [])
    ];

    // Ensure "Others" is always the last in the final array
    finalData = finalData.sort((a, b) => {
        if (a.associationTags === 'Others') return -1; // "Others" should always come last
        if (b.associationTags === 'Others') return 1;
        return a.count - b.count; // Keep the remaining sorted by count
    });

    // Prepare data for the chart
    const data = finalData.map(item => ({
        associationTags: item.associationTags,
        count: item.count,
        color: selectedGrievanceByAssociationCat === item.associationTags ? chart_color_palette.secondarylight : chart_color_palette.secondarylight // Adjust color if needed
    }));

    // Return chart configuration
    return {
        title: {
            text: appsmith.URL.queryParams.lng === 'te' ? 'సంఘం వర్గం ఆధారంగా ఫిర్యాదులు' : 'Top 5 Association Categories for Grievances Registered',
            left: 'center',
            top: 'top',
            textStyle: {
                fontSize: 18,
                fontWeight: 'bold'
            }
        },
        grid: {
            left: '20%',
            right: '10%',
            top: '10%',
            bottom: '15%'
        },
        xAxis: {
            type: 'value',
            nameTextStyle: {
                fontSize: 14,
                fontWeight: 'bold'
            }
        },
        yAxis: {
            type: 'category', // Y-axis represents categories
            data: data.map(item => item.associationTags),
            axisLabel: {
                formatter: function(value) {
                    const maxLength = 10; // Max characters per line
                    const words = value.split(' '); // Split by spaces
                    let line = '';
                    let result = '';

                    words.forEach(word => {
                        if ((line + word).length > maxLength) { // Corrected string concatenation logic
                            result += `${line.trim()}\n`; // Add line break
                            line = word; // Start new line
                        } else {
                            line += (line.length === 0 ? '' : ' ') + word; // Add word to current line
                        }
                    });

                    result += line.trim(); // Add the last line
                    return result;
                }
            }
        },
        series: [
            {
                type: 'bar',
                data: data.map(item => ({
                    value: item.count,
                    itemStyle: {
                        color: item.color,
                        opacity: selectedGrievanceByAssociationCat && selectedGrievanceByAssociationCat !== item.associationTags ? 0.2 : 1
                    }
                })),
                label: {
                    show: true,
                    position: 'right',
                    formatter: '{c}',
                    fontSize: 12
                }
            }
        ],
        tooltip: {
            trigger: 'axis',
            axisPointer: {
                type: 'shadow'
            }
        }
    };
})()
))(grievance_by_associ_cat_chart.chartType) }}
