{{ ((chartType) => ( (() => {
    // Retrieve selected filter values
    const { selectGrievanceByIndiv_cat, chart_color_palette } = utills.getFilters();

    // Filter the dataset based on selected filters
    const filteredData = utills.filterDataPoints({
        data: grievancedataset_tnl.data,
        filterTypes: '*',
        excludeTypes: ['ByIndivCat', 'TimeLine']
    });

    // Use query binding to get individual tags
    const individualTagsData = individual_tags_check_tnl.data || []; // Replace with your actual query name

    // Extract association tags to exclude from the array of objects
    const individualTagsSet = new Set(
        individualTagsData.map(item => item.Tags ? item.Tags.trim() : '') // Adjust 'Tags' based on your actual property
    );
    console.log('Individual tags:', individualTagsSet);

    // Aggregate data by association tags and count distinct grievances for all tags
    const aggregatedData = filteredData.reduce((acc, req) => {
        const uniqueGrievanceKey = req.grievance_key;
        req.requestor_tags?.forEach(tag => {
            const trimmedTag = tag.trim();
            if (individualTagsSet.has(trimmedTag)) { // Only include tags present in individualTagsSet
                if (!acc[trimmedTag]) {
                    acc[trimmedTag] = new Set(); // Create a Set for distinct grievance keys
                }
                acc[trimmedTag].add(uniqueGrievanceKey); // Add the grievance key to the Set
            }
        });
        return acc;
    }, {});

    console.log('Aggregated tags:', aggregatedData);

    // Convert the aggregated data into an array and count distinct grievances
    const dataArray = Object.entries(aggregatedData)
        .map(([associationTags, grievanceSet]) => ({
            associationTags,
            count: grievanceSet.size
        }));

    // Sort data by count in descending order
    const sortedData = dataArray.sort((a, b) => b.count - a.count);

    // Now take the top 5 from the sorted data
    const top5 = sortedData.slice(0, 5); // Get the top 5 categories from the aggregated data
    const others = sortedData.slice(5); // Get all remaining categories

    // Aggregate the count of the "Other" categories
    const otherCount = others.reduce((acc, curr) => acc + curr.count, 0);

    // Prepare the final data, adding "Other" as the last category if there are any remaining categories
    const finalData = [
        ...top5,
        ...(otherCount > 0 ? [{ associationTags: 'Other', count: otherCount }] : []) // 'Other' added at the end
    ];
    console.log('finalData', finalData);

    // Prepare data for the chart
    const data = finalData.sort((a, b) => a.count - b.count).map((item) => ({
        associationTags: item.associationTags,
        count: item.count,
        color: chart_color_palette.secondarylight // Use secondarylight as default color
    }));

    // Return chart configuration
    return {
        title: {
            text: appsmith.URL.queryParams.lng === 'te' ? 'వ్యక్తిగత వర్గం ఆధారంగా ఫిర్యాదులు' : 'Top 5 Individual Categories for Grievances Registered',
            left: 'center',
            top: 'top',
            textStyle: {
                fontSize: 18,
                fontWeight: 'bold'
            }
        },
        grid: {
            left: '20%',
            right: '10%',
            top: '10%',
            bottom: '15%'
        },
        xAxis: {
            type: 'value',
            nameTextStyle: {
                fontSize: 14,
                fontWeight: 'bold'
            }
        },
        yAxis: {
            type: 'category', // Y-axis represents categories
            data: data.map(item => item.associationTags),
            axisLabel: {
                formatter: function(value) {
                    const maxLength = 10; // Max characters per line
                    const words = value.split(' '); // Split by spaces
                    let line = '';
                    let result = '';

                    words.forEach(word => {
                        if ((line + word).length > maxLength) { // Corrected concatenation logic
                            result += `${line.trim()}\n`; // Add line break
                            line = word; // Start new line
                        } else {
                            line += (line.length === 0 ? '' : ' ') + word; // Add word to current line
                        }
                    });

                    result += line.trim(); // Add the last line
                    return result;
                }
            }
        },
        series: [
            {
                type: 'bar',
                data: data.map(item => ({
                    value: item.count,
                    itemStyle: {
                        color: item.color,
                        opacity: selectGrievanceByIndiv_cat && selectGrievanceByIndiv_cat !== item.associationTags ? 0.2 : 1
                    }
                })),
                label: {
                    show: true,
                    position: 'right',
                    formatter: '{c}',
                    fontSize: 12
                }
            }
        ],
        tooltip: {
            trigger: 'axis',
            axisPointer: {
                type: 'shadow'
            }
        }
    };
})()
))(grievance_by_indiv_cat_chart.chartType) }}
