{{ ((chartType) => ( (() => {
    // Retrieve selected filter values
    const { selectedGrievanceRequestType, chart_color_palette } = utills.getFilters();

    // Filter the dataset based on selected filters
    const filteredData = utills.filterDataPoints({
        data: grievancedataset_tnl.data,
        filterTypes: '*',
        excludeTypes: ['RequestType', 'TimeLine']
    });

    // Aggregate data by grievance type and count grievances
    const aggregatedData = filteredData.reduce((acc, req) => {
        const grievanceType = req.grievance_type || 'Unknown';
        acc[grievanceType] = (acc[grievanceType] || 0) + 1; // Fixed increment logic
        return acc;
    }, {});

    // Convert aggregated data into an array, filter, and sort it
    const sortedData = Object.entries(aggregatedData)
        .map(([grievance_type, count]) => ({
            grievance_type,
            count
        }))
        .filter(item => item.grievance_type && item.grievance_type.trim() !== '' && item.grievance_type.trim() !== 'Unknown') // Exclude null or empty grievance_type
        .sort((a, b) => b.count - a.count); // Sort in descending order by count

    // Take the top 5 grievance types
    const top5 = sortedData.slice(0, 5);
    const othersCount = sortedData.slice(5).reduce((acc, curr) => acc + curr.count, 0); // Count for "Others"

    // Prepare final data including "Others" if there's any count
    const finalData = [
        ...top5,
        ...(othersCount > 0 ? [{ grievance_type: 'Others', count: othersCount }] : [])
    ];

    // Prepare data for the chart
    const data = finalData
        .sort((a, b) => a.count - b.count) // Sort in ascending order based on count for correct representation in the chart
        .sort((a, b) => a.grievance_type === "Others" ? -1 : 1) // Move "Others" to the end
        .map(item => ({
            grievance_type: item.grievance_type,
            count: item.count,
            color: selectedGrievanceRequestType === item.grievance_type ? chart_color_palette.secondarylight : chart_color_palette.secondarylight // Adjust as needed
        }));

    return {
        title: {
            text: appsmith.URL.queryParams.lng === 'te' ? 'ఫిర్యాదుల రకాల ఆధారంగా ఫిర్యాదుల విభజన' : 'Most Reported Grievance Types: Top 5',
            left: 'center',
            top: 'top',
            textStyle: {
                fontSize: 18,
                fontWeight: 'bold'
            }
        },
        grid: {
            left: '20%',
            right: '10%',
            top: '10%',
            bottom: '15%'
        },
        xAxis: {
            type: 'value',
            splitLine: {
                show: true
            }
        },
        yAxis: {
            type: 'category',
            data: data.map(item => item.grievance_type),
            axisLabel: {
                formatter: function (value) {
                    const maxLength = 15; // Max characters per line
                    const words = value.split(' '); // Split by spaces
                    let line = '';
                    let result = '';

                    words.forEach(word => {
                        if ((line + word).length > maxLength) {
                            result += line.trim() + '\n'; // Add line break
                            line = word; // Start new line
                        } else {
                            line += (line.length === 0 ? '' : ' ') + word; // Add word to current line
                        }
                    });

                    result += line.trim(); // Add the last line
                    return result;
                }
            }
        },
        series: [
            {
                type: 'bar',
                data: data.map(item => ({
                    value: item.count,
                    itemStyle: {
                        color: item.color,
                        opacity: selectedGrievanceRequestType && selectedGrievanceRequestType !== item.grievance_type ? 0.2 : 1
                    }
                })),
                label: {
                    show: true,
                    position: 'right',
                    formatter: '{c}',
                    fontSize: 12
                }
            }
        ],
        tooltip: {
            trigger: 'axis',
            axisPointer: {
                type: 'shadow'
            },
            formatter: function(params) {
                const data = params[0];
                return `${data.name}: ${data.value}`; // Show grievance type and count in tooltip
            }
        }
    };
})()
))(grievance_request_type_chart.chartType) }}
