{{ ((chartType) => ( (() => {
    // Retrieve selected filter values
    const { selectGrievanceByConstituency, chart_color_palette } = utills.getFilters();

    // Filter the dataset based on selected filters
    const filteredData = utills.filterDataPoints({
        data: grievancedataset_tnl.data,
        filterTypes: '*',
        excludeTypes: ['ByConstituency', 'TimeLine']
    });

    // Aggregate data by constituency and count grievances
    const aggregatedData = filteredData
        .filter(item => 
            item.location_granularity === 'AC' || 
            item.location_granularity === 'Mandal' || 
            item.location_granularity === 'Village'
        )
        .reduce((acc, req) => {
            const constituencyName = req.request_ac_name || 'Unknown'; // Use 'Unknown' for missing names
            acc[constituencyName] = (acc[constituencyName] || 0) + 1; // Increment count for each constituency
            return acc; // Return the accumulator
        }, {});

    // Convert the aggregated data into an array, sort by grievance count
    const sortedData = Object.entries(aggregatedData)
        .map(([constituencyName, count]) => ({ constituencyName, count }))
        .sort((a, b) => b.count - a.count); // Sort in descending order by count

    // Take the top 5 and aggregate the rest as "Others"
    const top5 = sortedData
        .filter(item => item.constituencyName && item.constituencyName.trim() !== '' && item.constituencyName.trim() !== 'NA') // Exclude null or empty names
        .slice(0, 5); // Get the top 5 categories

    // Aggregate all other entries as "Others"
    const othersCount = sortedData
        .filter(item => !top5.some(topItem => topItem.constituencyName === item.constituencyName)) // Filter out the top 5
        .reduce((acc, curr) => acc + curr.count, 0); // Sum up counts for "Others"

    // Prepare final data, ensuring "Others" is always included at the end if there's any count
    const finalData = [
        ...top5,
        ...(othersCount > 0 ? [{ constituencyName: 'Others', count: othersCount }] : [])
    ];

    // Prepare data for the chart
    const data = finalData
        .sort((a, b) => a.count - b.count) // Sort in ascending order based on count for correct representation in the chart
        .sort((a, b) => a.constituencyName === "Others" ? -1 : 1) // Move "Others" to the end
        .map(item => ({
            value: item.count, // X-axis value for horizontal bar chart
            itemStyle: {
                color: chart_color_palette.secondarylight, // Use secondarylight as default color
                opacity: selectGrievanceByConstituency && selectGrievanceByConstituency !== item.constituencyName ? 0.2 : 1
            }
        }));

    const yAxisData = finalData.map(item => item.constituencyName); // Prepare y-axis data without special treatment for "Others"

    return {
        title: {
            text: appsmith.URL.queryParams.lng === 'te' ? 'ఫిర్యాదుల ఆధారంగా అగ్రస్థానంలో ఉన్న 5 అసెంబ్లీ నియోజకవర్గాలు' : 'Top 5 Assembly Constituencies with Reported Grievances',
            left: 'center',
            top: 'top',
            textStyle: {
                fontSize: 18,
                fontWeight: 'bold'
            }
        },
        grid: {
            left: '20%',
            right: '10%',
            top: '10%',
            bottom: '15%'
        },
        xAxis: {
            type: 'value'
        },
        yAxis: {
            type: 'category',
            data: yAxisData, // Set categories for y-axis
            axisLabel: {
                formatter: function (value) {
                    const maxLength = 10; // Max characters per line
                    const words = value.split(' '); // Split by spaces
                    let line = '';
                    let result = '';

                    words.forEach(word => {
                        if ((line + word).length > maxLength) {
                            result += `${line.trim()}\n`; // Add line break
                            line = word; // Start new line
                        } else {
                            line += (line.length === 0 ? '' : ' ') + word; // Add word to current line
                        }
                    });

                    result += line.trim(); // Add the last line
                    return result;
                }
            }
        },
        series: [
            {
                type: 'bar', // Bar chart type
                data: data,
                label: {
                    show: true,
                    position: 'right',
                    formatter: '{c}', // Display the value
                    fontSize: 12 // Show labels on the right side of the bars
                }
            }
        ],
        tooltip: {
            trigger: 'axis',
            axisPointer: {
                type: 'shadow'
            }
        }
    };
})()
))(grievances_constit_wise_chart.chartType) }}
