{{ ((chartType) => ( (() => {
    // Retrieve selected filter values
    const { selectGrievanceByMandal, chart_color_palette } = utills.getFilters();

    // Filter the dataset based on selected filters
    const filteredData = utills.filterDataPoints({
        data: grievancedataset_tnl.data,
        filterTypes: '*',
        excludeTypes: ['ByMandal', 'TimeLine']
    });

    // Aggregate data by mandal and count grievances
    const aggregatedData = filteredData
        .filter(req => req.location_granularity === 'Mandal') // Exclude entries with 'NA' mandal names
        .reduce((acc, req) => {
            const mandalName = req.request_mandal || 'Unknown'; // Handle undefined mandal names
            acc[mandalName] = (acc[mandalName] || 0) + 1; // Increment count for each mandal
            return acc; // Ensure to return the accumulator
        }, {});

    // Convert the aggregated data into an array and sort by grievance count
    const sortedData = Object.entries(aggregatedData)
        .map(([mandalName, count]) => ({ mandalName, count }))
        .sort((a, b) => b.count - a.count); // Sort in descending order by count

    // Take the top 5 mandals and aggregate the rest as "Others"
    const top5 = sortedData
        .filter(item => item.mandalName && item.mandalName.trim() !== '' && item.mandalName.trim() !== 'NA') // Exclude null or empty names
        .slice(0, 5); // Get the top 5 mandals

    // Count the number of grievances for "Others"
    const othersCount = sortedData
        .filter(item => !top5.some(topItem => topItem.mandalName === item.mandalName)) // Filter out the top 5
        .reduce((acc, curr) => acc + curr.count, 0); // Sum up counts for "Others"

    // Prepare final data
    const finalData = [
        ...top5,
        ...(othersCount > 0 ? [{ mandalName: 'Others', count: othersCount }] : [])
    ];

    // Prepare data for the chart with dynamic color assignment
    const data = finalData
        .sort((a, b) => a.count - b.count) // Sort in ascending order based on count
        .sort((a, b) => a.mandalName === "Others" ? 1 : -1) // Move "Others" to the end
        .map(item => ({
            mandalName: item.mandalName,
            count: item.count,
            itemStyle: {
                color: chart_color_palette.secondarylight, // Use secondarylight as default color
                opacity: selectGrievanceByMandal && selectGrievanceByMandal !== item.mandalName ? 0.2 : 1 // Adjust opacity
            }
        }));

    const yAxisData = finalData.map(item => item.mandalName); // No special treatment for "Others"

    return {
        title: {
            text: appsmith.URL.queryParams.lng === 'te' ? 'ఫిర్యాదుల ఆధారంగా అగ్రస్థానంలో ఉన్న 5 మండలాలు' : 'Top 5 Mandals with Reported Grievances',
            left: 'center',
            top: 'top',
            textStyle: {
                fontSize: 18,
                fontWeight: 'bold'
            }
        },
        grid: {
            left: '20%',
            right: '10%',
            top: '10%',
            bottom: '15%'
        },
        xAxis: {
            type: 'value'
        },
        yAxis: {
            type: 'category',
            data: yAxisData,
            axisLabel: {
                formatter: function (value) {
                    const maxLength = 10; // Max characters per line
                    const words = value.split(' '); // Split by spaces
                    let line = '';
                    let result = '';

                    words.forEach(word => {
                        if ((line + word).length > maxLength) {
                            result += `${line.trim()}\n`; // Add line break
                            line = word; // Start new line
                        } else {
                            line += (line.length === 0 ? '' : ' ') + word; // Add word to current line
                        }
                    });

                    result += line.trim(); // Add the last line
                    return result;
                }
            }
        },
        series: [
            {
                type: 'bar',
                data: data.map(item => ({
                    value: item.count,
                    itemStyle: item.itemStyle // Use the item style with color and opacity
                })),
                label: {
                    show: true,
                    position: 'right',
                    formatter: '{c}', // Display the value
                    fontSize: 12 // Show labels on the right side of the bars
                }
            }
        ],
        tooltip: {
            trigger: 'axis',
            axisPointer: {
                type: 'shadow'
            }
        }
    };
})()))(grievances_mandal_wise_chart.chartType) }}
