{{ ((chartType) => ( (() => {
    // Retrieve selected filter values
    const { chart_color_palette } = utills.getFilters();

    // Filter the dataset based on selected filters
    const filteredData = utills.filterDataPoints({
        data: grievancedataset_tnl.data,
        filterTypes: '*',
        excludeTypes: ['TimeLine']
    });

    // Function to format the date as DD/Mon/YYYY
    function formatDate(dateStr) {
        const date = new Date(dateStr);
        const options = { day: '2-digit', month: 'short', year: 'numeric' };
        return date.toLocaleDateString('en-GB', options); // Formats date as DD/Mon/YYYY
    }

    // Aggregate data by date
    const aggregatedData = filteredData
        .filter(req => ["In Progress", "Draft", "Verified", "In Review", "Send For Correction"].includes(req.grievance_status))
        .reduce((acc, entry) => {
            const date = formatDate(entry.created_on); // Use correct field for date
            acc[date] = (acc[date] || 0) + 1; // Count the number of grievances per date
            return acc;
        }, {});

    // Convert aggregated data to the format required for the chart and sort by date
    const data = Object.entries(aggregatedData).map(([date, number_of_grievances]) => ({
        date,
        value: number_of_grievances
    })).sort((a, b) => {
        const dateA = new Date(a.date.split('/').reverse().join('-')); // Convert DD/Mon/YYYY to YYYY-MM-DD
        const dateB = new Date(b.date.split('/').reverse().join('-')); // Convert DD/Mon/YYYY to YYYY-MM-DD
        return dateA - dateB; // Sort by date
    });

    return {
        title: {
            text: appsmith.URL.queryParams.lng === 'te' ? 'సమయానుసారం ఫిర్యాదుల సంఖ్య' : 'Grievance Registrations Trend over Time',
            left: 'center',
            top: 'top',
            textStyle: {
                fontSize: 18,
                fontWeight: 'bold'
            }
        },
        tooltip: {
            trigger: 'axis',
            formatter: '{b}: {c}'
        },
        grid: {
            left: '5%',   // Increase the left margin to create space for the Y-axis title
            right: '5%',  // Adjust right margin
            top: '20%',   // Adjust top margin
            bottom: '10%' // Adjust bottom margin
        },
        xAxis: {
            type: 'category',
            data: data.map(item => item.date),
            boundaryGap: false
        },
        yAxis: {
            type: 'value',
            name: 'No. of Grievances',
            nameLocation: 'middle',  // Position the Y-axis title in the middle
            nameRotate: 90,          // Rotate the title vertically
            nameTextStyle: {
                fontSize: 14,
                padding: [0, 10, 10, 0] // Adjust padding to control spacing
            }
        },
        series: [
            {
                type: 'line',
                data: data.map(item => item.value),
                itemStyle: {
                    color: chart_color_palette.lineColor || '#EEC861' // Use chart_color_palette or default color
                },
                lineStyle: {
                    width: 2
                },
                smooth: true,
                label: {
                    show: true,
                    formatter: '{c}',
                    position: 'top'
                }
            }
        ]
    };
})()
))(grievances_over_time.chartType) }}
