{{ ((chartType) => ( (() => {
    // Filter the dataset based on selected filters
    const filteredData = utills.filterDataPoints({
        data: grievancedataset_tnl.data,
        filterTypes: '*',
        excludeTypes: ['Priority', 'TimeLine']
    });

    // Filter only the relevant statuses
    const relevantStatuses = ["In Progress", "Draft", "Verified", "In Review", "Send For Correction"];
    const statusFilteredData = filteredData.filter(req => relevantStatuses.includes(req.grievance_status));

    // Aggregate the filtered data by priority
    const aggregatedData = statusFilteredData.reduce((acc, req) => {
        const priority = req.priority || 'Unknown';
        acc[priority] = (acc[priority] || 0) + 1; // Increment count for each priority
        return acc;
    }, {});

    // Prepare the data for the chart
    const chartData = Object.entries(aggregatedData).map(([priority, count]) => ({
        name: priority,
        value: count,
        itemStyle: {
            color: priority === 'High' ? '#D32F2F' :    // Red for High priority
                   priority === 'Medium' ? '#EEC861' :  // Yellow for Medium priority
                   '#0000FF'  // Blue for other priorities
        }
    }));

    // Return the final chart configuration
    return {
        title: {
            text: appsmith.URL.queryParams.lng === 'te' ? 'ప్రాధాన్యత ఆధారంగా ఫిర్యాదులు' : 'Grievances By Priority',  // Dynamic title based on language
            left: 'center',
            textStyle: {
                fontSize: 18,
                fontWeight: 'bold'
            }
        },
        series: [
            {
                type: 'pie',  // Pie chart type
                radius: ['30%', '50%'],  // Inner and outer radius of the pie chart
                data: chartData,  // Data for the pie chart
                label: {
                    show: true,
                    formatter: '{b}: {c} ({d}%)'  // Display name and percentage
                },
                emphasis: {
                    itemStyle: {
                        shadowBlur: 10,
                        shadowOffsetX: 0,
                        shadowColor: 'rgba(0, 0, 0, 0.5)'  // Shadow effect on hover
                    }
                }
            }
        ],
        tooltip: {
            trigger: 'item',
            formatter: '{b}: {c} ({d}%)'  // Tooltip formatting to show name, count, and percentage
        }
    };
})()
))(priority_chart.chartType) }}
