{{ ((chartType) => ( 
  (() => {
    // Retrieve selected filter values
    const { selectedGrievanceName, chart_color_palette } = party_cader_utils.getFilters();

    // Filter the dataset based on selected filters and exclude null grievance names
    const filteredData = party_cader_utils.filterDataPoints({
      data: party_cader_dataset_tnl.data,
      filterTypes: '*',
      excludeTypes: [] // Exclude no types
    }).filter(req => req.requestor_grievances_name !== null); // Exclude null values

    // Aggregate data by unique grievance name and count grievances
    const aggregatedData = filteredData.reduce((acc, req) => {
      const grievanceName = req.requestor_grievances_name;
      if (!acc[grievanceName]) {
        acc[grievanceName] = new Set(); // Use a Set to track unique grievances
      }
      acc[grievanceName].add(req); // Add the request to the Set
      return acc;
    }, {});

    // Convert the aggregated data into an array and count unique grievances
    const sortedData = Object.entries(aggregatedData)
      .map(([grievanceName, requests]) => ({
        grievanceName,
        count: requests.size // Count of unique grievances
      }))
      .sort((a, b) => b.count - a.count) // Sort by count descending
      .slice(0, 5) // Take the top 5 grievances
      .sort((a, b) => a.count - b.count); // Sort the top 5 by count ascending for yAxis

    // Prepare data for the chart
    const data = sortedData.map(item => ({
      grievanceName: item.grievanceName,
      count: item.count,
      color: selectedGrievanceName.includes(item.grievanceName) ? chart_color_palette.secondarylight : chart_color_palette.secondarylight // Ensure correct color assignment
    }));

    return {
      title: {
        text: 'Top 5 Most Frequently Registered Grievance Types by Cadre',
        left: 'center',
        top: 'top',
        textStyle: {
          fontSize: 18,
          fontWeight: 'bold'
        }
      },
      grid: {
        left: '15%',
        right: '10%',
        top: '10%',
        bottom: '15%'
      },
      xAxis: {
        type: 'value',
        name: 'Count'
      },
      yAxis: {
        type: 'category',
        data: data.map(item => item.grievanceName),
        axisLabel: {
          formatter: function(value) {
            const maxLength = 20; // Maximum length of characters per line
            const words = value.split(' '); // Split the label into words
            let line = '';
            let result = '';

            for (const word of words) {
              if ((line.length + word.length + 1) > maxLength) { // Check total length including space
                result += line.trim() + '\n'; // Add line break
                line = word; // Start new line
              } else {
                line += (line.length === 0 ? '' : ' ') + word; // Add word to current line
              }
            }
            result += line.trim(); // Add the last line
            return result;
          }
        }
      },
      series: [
        {
          type: 'bar',
          data: data.map(item => ({
            value: item.count,
            itemStyle: {
              color: item.color,
              opacity: selectedGrievanceName.length > 0 && !selectedGrievanceName.includes(item.grievanceName) ? 0.2 : 1 // Highlight if not selected
            }
          })),
          label: {
            show: true,
            position: 'right',
            formatter: '{c}', // Display the count
            fontSize: 12
          }
        }
      ],
      tooltip: {
        trigger: 'axis',
        axisPointer: {
          type: 'shadow'
        }
      }
    };
  })()
))(grievance_name_chart.chartType) }}
