{{ ((chartType) => ( 
  (() => {
    // Retrieve selected filter values
    const { selectedCaderMandal, chart_color_palette } = party_cader_utils.getFilters();

    // Filter the dataset based on selected filters
    const filteredData = party_cader_utils.filterDataPoints({
      data: party_cader_dataset_tnl.data,
      filterTypes: '*',
      excludeTypes: ['CaderMandal'] // Adjusted to include all data
    });

    // Aggregate data by mandal and count grievances
    const aggregatedData = filteredData.reduce((acc, req) => {
      const mandal = req.mandal;
      if (!acc[mandal]) {
        acc[mandal] = 0;
      }
      acc[mandal] += 1;
      return acc;
    }, {});

    // Convert the aggregated data into an array and sort by grievance count
    const sortedData = Object.entries(aggregatedData)
      .map(([mandal, count]) => ({ mandal, count }))
      .sort((a, b) => b.count - a.count) // Sort by count descending
      .slice(0, 5) // Take the top 5 mandals
      .sort((a, b) => a.count - b.count); // Sort the top 5 by count ascending for yAxis

    // Prepare data for the chart
    const data = sortedData.map(item => ({
      mandal: item.mandal,
      count: item.count,
      color: selectedCaderMandal.includes(item.mandal) ? chart_color_palette.secondarylight : chart_color_palette.secondarylight // Ensure correct color assignment
    }));

    return {
      title: {
        text: 'Top 5 Mandals for Active Cadre-Driven Grievances',
        left: 'center',
        top: 'top',
        textStyle: {
          fontSize: 18,
          fontWeight: 'bold'
        }
      },
      grid: {
        left: '20%', // Increase left margin to fit labels
        right: '10%',
        top: '10%',
        bottom: '15%'
      },
      xAxis: {
        type: 'value', // X-axis represents values for a horizontal bar chart
        name: 'Count'
      },
      yAxis: {
        type: 'category', // Y-axis represents categories (mandal names)
        data: data.map(item => item.mandal), // Set categories (mandal names)
        axisLabel: {
          formatter: function(value) {
            const maxLength = 10; // Number of characters per line
            const words = value.split(' '); // Split by spaces
            let line = '';
            let result = '';

            for (const word of words) {
              if ((line.length + word.length + 1) > maxLength) {
                result += line.trim() + '\n'; // Add line and newline character
                line = word; // Start a new line
              } else {
                line += (line.length === 0 ? '' : ' ') + word; // Add word to current line
              }
            }
            result += line.trim(); // Add the last line
            return result;
          }
        }
      },
      series: [
        {
          type: 'bar', // Bar chart type
          data: data.map(item => ({
            value: item.count, // X-axis value for horizontal bar chart
            itemStyle: {
              color: item.color,
              opacity: selectedCaderMandal.length > 0 && !selectedCaderMandal.includes(item.mandal) ? 0.2 : 1 // Highlight if selected
            }
          })),
          label: {
            show: true,
            position: 'right',
            formatter: '{c}', // Display the value
            fontSize: 12 // Show labels on the right side of the bars
          }
        }
      ],
      tooltip: {
        trigger: 'axis',
        axisPointer: {
          type: 'shadow'
        }
      }
    };
  })()
))(mandal_cader_griev_chart.chartType) }}
