{{ ((chartType) => ( (() => {
    // Retrieve selected filter values
    const { selectedGender } = requestor_utils.getFilters();

    // Filter the dataset based on selected filters
    const filteredData = requestor_utils.filterDataPoints({
        data: requestordataset_tnl.data,
        filterTypes: '*',
        excludeTypes: ['ByGender']
    });

    // Aggregate data
    const aggregatedData = filteredData.reduce((acc, item) => {
        acc[item.gender] = (acc[item.gender] || 0) + 1; // Correctly increment the count by gender
        return acc;
    }, {});

    // Sort and prepare data for the chart
    const sortedData = Object.entries(aggregatedData)
        .sort((a, b) => b[1] - a[1]) // Sort by count in descending order
        .map(([gender, count]) => ({
            name: gender,
            value: count,
            itemStyle: {
                opacity: selectedGender && selectedGender !== gender ? 0.2 : 1
            }
        }));

    return {
        title: {
            text: 'Requestors by Gender',
            left: 'center',
            top: 'top',
            textStyle: {
                fontSize: 18,
                fontWeight: 'bold',
            },
        },
        series: [
            {
                type: 'pie',
                radius: ['30%', '60%'],
                data: sortedData,
                label: {
                    show: true,
                    formatter: '{b}: {d}%',
                },
                emphasis: {
                    itemStyle: {
                        shadowBlur: 10,
                        shadowOffsetX: 0,
                        shadowColor: 'rgba(0, 0, 0, 0.5)'
                    }
                }
            }
        ],
        tooltip: {
            trigger: 'item',
            formatter: '{b}: {c} ({d}%)'
        }
    };
})()
))(gender_chart.chartType) }}
