(() => {
  // Filter the dataset based on selected filters
  const filteredData = requestor_utils.filterDataPoints({
    data: requestordataset_tnl.data,
    filterTypes: '*',
    excludeTypes: ['AssociCats']
  });
  // Step 3: Create a unique list of requestor type names and codes from the filtered data
const association_cat = _.uniqBy(
  _.flatten(
    filteredData.filter(req =>req.requestor_type_name !== 'Individual').map(item => 
      item.tags
        .filter(tag => tag && tag.trim() !== '' && tag !== 'None' && tag !== 'NA')  // Filter out unwanted tags
        .map(tag => ({
          name: tag,  // Get tag name
          code: tag   // Set code to tag name for uniqueness
        }))
    )
  ),
  'code'  // Ensure uniqueness by 'code'
);

  return association_cat;  // Return the result as a unique list of requestor types
})()
}}
