{{ ((chartType) => ( 
(() => {
	// Retrieve selected filter values
	const { selectedAssociationCat, chart_color_palette } = requestor_utils.getFilters();

	// Filter the dataset based on selected filters
	const filteredData = requestor_utils.filterDataPoints({
		data: requestordataset_dev.data,
		filterTypes: '*',
		excludeTypes: ['ByAssocicat']
	});

	// Aggregate data by tag and count occurrences
	const aggregatedData = filteredData
	.filter(item => item.requestor_type_name === 'Association')
	.reduce((acc, item) => {
		item.tags
			.filter(tag => tag != null && tag !== 'None' && tag !== '')
			.forEach(tag => {
			if (selectedAssociationCat.length === 0 || selectedAssociationCat.includes(tag)) {
				acc[tag] = (acc[tag] || 0) + 1; // Correct increment logic
			}
		});
		return acc;
	}, {});

	// Convert the aggregated data into an array, sort by count, and take the top 5
	const data = Object.entries(aggregatedData)
	.map(([tag, count]) => ({
		tag,
		count,
		color: selectedAssociationCat.includes(tag) ? chart_color_palette.secondarylight : chart_color_palette.secondarylight // Differentiate between selected and unselected
	}))
	.sort((a, b) => b.count - a.count) // Sort in descending order by count
	.slice(0, 5) // Take the top 5
	.sort((a, b) => a.count - b.count); // Sort the top 5 in ascending order by count

	// Prepare data for the chart
	return {
		title: {
			text: 'Top 5 Association Categories as Requestors',
			left: 'center',
			top: 'top',
			textStyle: {
				fontSize: 18,
				fontWeight: 'bold'
			}
		},
		grid: {
			left: '20%',
			right: '10%',
			top: '10%',
			bottom: '15%'
		},
		xAxis: {
			type: 'value'
		},
		yAxis: {
			type: 'category',
			data: data.map(item => item.tag),
			axisLabel: {
				formatter: function(value) {
					const maxLength = 10; // Number of characters per line
					const words = value.split(' '); // Split by spaces
					let line = '';
					let result = '';

					for (const word of words) {
						if ((line + word).length > maxLength) {
							result += line.trim() + '\n'; // Add line break
							line = word; // Start new line
						} else {
							line += (line.length === 0 ? '' : ' ') + word; // Add word to current line
						}
					}
					result += line.trim(); // Add the last line
					return result;
				}
			}
		},
		series: [
			{
				type: 'bar',
				data: data.map(item => ({
					value: item.count,
					itemStyle: {
						color: item.color,
						opacity: selectedAssociationCat && !selectedAssociationCat.includes(item.tag) ? 0.2 : 1 // Highlight selected
					}
				})),
				label: {
					show: true,
					position: 'right',
					formatter: '{c}', // Display the value
					fontSize: 12
				}
			}
		],
		tooltip: {
			trigger: 'axis',
			axisPointer: {
				type: 'shadow'
			}
		}
	};
})()
))(req_association_cat_chart.chartType) }}
