{{ ((chartType) => ( (() => {
    // Retrieve selected filter values
    const { selectedRequestorTypeName, chart_color_palette } = requestor_utils.getFilters();

    // Filter the dataset based on selected filters
    const filteredData = requestor_utils.filterDataPoints({
        data: requestordataset_tnl.data,
        filterTypes: '*',
        excludeTypes: ['ByRequestorType']
    });

    // Aggregate the data by requestor type
    const data = filteredData.reduce((acc, requestor) => {
        const key = requestor.requestor_type_name;
        acc[key] = (acc[key] || 0) + 1; // Correct increment logic
        return acc; // Ensure return of accumulated result
    }, {});

    console.log(data); // Log data to verify aggregation

    // Convert aggregated data to array format for chart
    const chartData = Object.entries(data).map(([requestorTypeName, count]) => ({
        requestor_type_name: requestorTypeName,
        count,
        color: selectedRequestorTypeName === requestorTypeName 
            ? chart_color_palette.secondarylight 
            : chart_color_palette.secondarylight // Differentiate colors if needed
    }));

    return {
        title: {
            text: 'Requestors By Type',
            left: 'center',
            top: 'top',
            textStyle: {
                fontSize: 18,
                fontWeight: 'bold'
            }
        },
        grid: {
            left: '20%',
            right: '10%',
            top: '10%',
            bottom: '15%'
        },
        xAxis: {
            type: 'value'
        },
        yAxis: {
            type: 'category',
            data: chartData.map(item => item.requestor_type_name),
            axisLabel: {
                formatter: function(value) {
                    const maxLength = 20; // Number of characters per line
                    const words = value.split(' '); // Split by spaces
                    let line = '';
                    let result = '';

                    words.forEach(word => {
                        if ((line + word).length > maxLength) {
                            result += line.trim() + '\n'; // Add line break
                            line = word; // Start a new line
                        } else {
                            line += (line.length === 0 ? '' : ' ') + word; // Add word to current line
                        }
                    });

                    result += line.trim(); // Add the last line
                    return result;
                }
            }
        },
        series: [
            {
                type: 'bar',
                data: chartData.map(item => ({
                    value: item.count,
                    itemStyle: {
                        color: item.color,
                        opacity: selectedRequestorTypeName && selectedRequestorTypeName !== item.requestor_type_name ? 0.2 : 1
                    }
                })),
                label: {
                    show: true,
                    position: 'right',
                    formatter: '{c}',
                    fontSize: 12
                }
            }
        ],
        tooltip: {
            trigger: 'axis',
            axisPointer: {
                type: 'shadow'
            }
        }
    };
})()
))(requestor_type_chart.chartType) }}
