customEChartConfig:
{{ ((chartType) => ( 
  (() => {
    // Retrieve selected filter values
    const { selectedMandal, chart_color_palette } = requestor_utils.getFilters();

    // Filter the dataset based on selected filters
    const filteredData = requestor_utils.filterDataPoints({
      data: requestordataset_dev.data,
      filterTypes: '*',
      excludeTypes: ['ByMandal']
    });

    // Aggregate data by mandal and count grievances
    const aggregatedData = filteredData
      .filter(req => req.mandal !== null && req.mandal !== 'NA' && req.mandal !== 'None')
      .reduce((acc, req) => {
        const mandalName = req.mandal;
        acc[mandalName] = (acc[mandalName] || 0) + 1; // Correct increment logic
        return acc;
      }, {});

    // Convert the aggregated data into an array and sort by grievance count
    const sortedData = Object.entries(aggregatedData)
      .map(([mandal_name, count]) => ({ mandal_name, count }))
      .sort((a, b) => b.count - a.count)  // Sort by count descending
      .slice(0, 5)  // Take the top 5 mandals
      .sort((a, b) => a.count - b.count);  // Sort by count ascending for yAxis

    // Prepare data for the chart
    const data = sortedData.map(item => ({
      mandal_name: item.mandal_name,
      count: item.count,
      color: selectedMandal === item.mandal_name ? chart_color_palette.secondarylight : chart_color_palette.secondarylight // Use different colors for selected and unselected mandals
    }));

    return {
      title: {
        text: 'Top 5 Mandals by Requestor Activity',  // Title text
        left: 'center',  // Position the title in the center
        top: 'top',  // Position the title at the top
        textStyle: {
          fontSize: 18,  // Set title font size
          fontWeight: 'bold'  // Set title font weight
        }
      },
      grid: {
        left: '20%',  // Increase left margin to fit labels
        right: '10%',
        top: '10%',
        bottom: '15%'
      },
      xAxis: {
        type: 'value'  // X-axis represents values for a horizontal bar chart
      },
      yAxis: {
        type: 'category',  // Y-axis represents categories (mandal names)
        data: data.map(item => item.mandal_name),  // Set categories (mandal names)
        axisLabel: {
          formatter: function(value) {
            const maxLength = 20; // Number of characters per line
            const words = value.split(' '); // Split by spaces
            let line = '';
            let result = '';

            for (const word of words) {
              if ((line + word).length > maxLength) {
                result += line.trim() + '\n'; // Add line break and newline character
                line = word; // Start a new line
              } else {
                line += (line.length === 0 ? '' : ' ') + word; // Add word to current line
              }
            }
            result += line.trim(); // Add the last line
            return result;
          }
        }
      },
      series: [
        {
          type: 'bar',  // Bar chart type
          data: data.map(item => ({
            value: item.count,  // X-axis value for horizontal bar chart
            itemStyle: {
              color: item.color,
              opacity: selectedMandal && selectedMandal !== item.mandal_name ? 0.2 : 1 // Highlight if selected
            }
          })),
          label: {
            show: true,
            position: 'right', 
            formatter: '{c}',   // Display the value
            fontSize: 12  // Show labels on the right side of the bars
          }
        }
      ],
      tooltip: {
        trigger: 'axis',
        axisPointer: {
          type: 'shadow'
        }
      }
    };
  })()
))(top5_mandal_by_requester_chart.chartType) }}
