{{ ((chartType) => ( (() => {
    // Retrieve selected filter values
    const { selectedRequester, chart_color_palette } = requestor_utils.getFilters();

    // Filter the dataset based on selected filters
    const filteredData = requestor_utils.filterDataPoints({
        data: requestordataset_tnl.data,
        filterTypes: '*',
        excludeTypes: ['ByReq_name']
    });

    // Aggregate data by unique combinations of requester name, AC, Mandal, Village, Mobile, and Requestor Type
    const aggregatedData = filteredData
        .filter(req => req.name != null && req.name !== 'NA' && req.name !== 'None')
        .reduce((acc, req) => {
            const key = `${req.name} \n(AC: ${req.constituency_name}, Mandal: ${req.mandal}, Village: ${req.village}, Mobile: ${req.mobile}) \n(${req.requestor_type_name})`;

            if (!acc[key]) {
                acc[key] = {
                    count: 0,
                    color: req.requestor_type_name === 'Association' ? chart_color_palette.secondarylight : chart_color_palette.secondarylight // Differentiate color for Association
                };
            }

            acc[key].count += 1; // Increment count
            return acc; // Ensure to return the accumulator
        }, {});

    // Convert the aggregated data into an array and sort by grievance count
    const sortedData = Object.entries(aggregatedData)
        .map(([key, data]) => ({
            name: key,
            count: data.count,
            color: data.color
        }))
        .sort((a, b) => b.count - a.count) // Sort by count descending
        .slice(0, 5) // Take the top 5 combinations
        .sort((a, b) => a.count - b.count); // Sort the top 5 by count ascending

    // Prepare data for the chart
    const data = sortedData.map(item => ({
        name: item.name,
        count: item.count,
        color: item.color
    }));

    return {
        title: {
            text: 'Top 5 Requestors with Highest Request Counts',
            left: 'center',
            top: 'top',
            textStyle: {
                fontSize: 18,
                fontWeight: 'bold'
            }
        },
        grid: {
            left: '30%',
            right: '10%',
            top: '10%',
            bottom: '15%'
        },
        xAxis: {
            type: 'value',
            name: 'Count'
        },
        yAxis: {
            type: 'category',
            data: data.map(item => item.name), // Set categories (unique combinations of name, AC, Mandal, Mobile, and Requestor Type)
            axisLabel: {
                formatter: function(value) {
                    const maxLength = 50; // Number of characters per line
                    const words = value.split(' '); // Split by spaces
                    let line = '';
                    let result = '';

                    for (const word of words) {
                        if ((line + word).length > maxLength) {
                            result += line.trim() + '\n'; // Add line and newline character
                            line = word; // Start a new line
                        } else {
                            line += (line.length === 0 ? '' : ' ') + word; // Add word to current line
                        }
                    }
                    result += line.trim(); // Add the last line
                    return result;
                }
            }
        },
        series: [
            {
                type: 'bar',
                data: data.map(item => ({
                    value: item.count,
                    itemStyle: {
                        color: item.color,
                        opacity: selectedRequester && selectedRequester !== item.name ? 0.2 : 1 // Highlight if selected
                    }
                })),
                label: {
                    show: true,
                    position: 'right',
                    formatter: '{c}', // Display the value
                    fontSize: 12
                }
            }
        ],
        tooltip: {
            trigger: 'axis',
            axisPointer: {
                type: 'shadow'
            }
        }
    };
})()
))(top5requesters.chartType) }}
