{{ ((chartType) => ( (() => {
    // Retrieve selected filter values
    const { selectedVillage, chart_color_palette } = requestor_utils.getFilters();

    // Filter the dataset based on selected filters
    const filteredData = requestor_utils.filterDataPoints({
        data: requestordataset_tnl.data,
        filterTypes: '*',
        excludeTypes: ['ByVillage']
    });

    // Aggregate data by village and count grievances
    const aggregatedData = filteredData
        .filter(req => req.village !== null && req.village !== 'NA' && req.village !== 'None')
        .reduce((acc, req) => {
            const villageName = req.village;
            acc[villageName] = (acc[villageName] || 0) + 1; // Correct increment logic
            return acc; // Ensure to return the accumulator
        }, {});

    // Convert the aggregated data into an array and sort by grievance count
    const sortedData = Object.entries(aggregatedData)
        .map(([village_name, count]) => ({ village_name, count }))
        .sort((a, b) => b.count - a.count)  // Sort by count descending
        .slice(0, 5)  // Take the top 5 villages
        .sort((a, b) => a.count - b.count);  // Sort by count ascending for yAxis

    // Prepare data for the chart
    const data = sortedData.map(item => ({
        village_name: item.village_name,
        count: item.count,
        color: selectedVillage === item.village_name ? chart_color_palette.secondarylight : chart_color_palette.secondarylight // Differentiate selected village color
    }));

    return {
        title: {
            text: 'Top 5 Villages by Requestor Activity',
            left: 'center',
            top: 'top',
            textStyle: {
                fontSize: 18,
                fontWeight: 'bold'
            }
        },
        grid: {
            left: '20%',
            right: '10%',
            top: '10%',
            bottom: '15%'
        },
        xAxis: {
            type: 'value'
        },
        yAxis: {
            type: 'category',
            data: data.map(item => item.village_name),
            axisLabel: {
                formatter: function(value) {
                    const maxLength = 20; // Number of characters per line
                    const words = value.split(' '); // Split by spaces
                    let line = '';
                    let result = '';

                    for (const word of words) {
                        if ((line + word).length > maxLength) {
                            result += line.trim() + '\n'; // Add line break and newline character
                            line = word; // Start a new line
                        } else {
                            line += (line.length === 0 ? '' : ' ') + word; // Add word to current line
                        }
                    }
                    result += line.trim(); // Add the last line
                    return result;
                }
            }
        },
        series: [
            {
                type: 'bar',
                data: data.map(item => ({
                    value: item.count,
                    itemStyle: {
                        color: item.color,
                        opacity: selectedVillage && selectedVillage !== item.village_name ? 0.2 : 1 // Highlight if selected
                    }
                })),
                label: {
                    show: true,
                    position: 'right',
                    formatter: '{c}', // Display the value
                    fontSize: 12
                }
            }
        ],
        tooltip: {
            trigger: 'axis',
            axisPointer: {
                type: 'shadow'
            }
        }
    };
})()
))(top5villages_by_requesters.chartType) }}
