(() => {
    // Filter the dataset based on selected filters
    const filteredData = requestor_utils.filterDataPoints({
        data: requestordataset_tnl.data,
        filterTypes: '*',
        excludeTypes: ['Village']
    });

    // Step 3: Create a unique list of requestor type names and codes
    const village_dropdown = _.uniqBy(
        filteredData
            .filter(item => item.village_key > 0) // Filter items with key greater than 0
            .map(item => ({ 
                name: item.village,      // Get requestor type name
                code: item.village_key   // Set code to requestor type name for constituency_name
            })), 
        'code'  // Ensure uniqueness by 'code'
    );

    return village_dropdown;  // Return the result as a unique list of requestor types
})()
}}
